import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import authApi from '../api/auth'
import pictureApi from '../api/pictures'

const Home: React.FC = () => {
    const [user, setUser] = useState<any>(null)
    const [pictures, setPictures] = useState<any>(null)
    const [selectedPicture, setSelectedPicture] = useState<any>(null)

    useEffect(() => {
        ;(async () => {
            const response = await authApi.auth()
            setUser(response)
            const pictures = await pictureApi.getPictures()
            setPictures(pictures)
        })()
    }, [])

    const selectPicture = () => {}

    return (
        <Container>
            <Header>{user?.username}</Header>
            <Body>
                <Left>
                    {pictures &&
                        pictures.map((pic: any) => (
                            <PictureElement key={pic.id}>
                                <div>{pic.score}</div>
                                {pic.thumbnail === 'nsfw' ? <div>Picture is NSFW</div> : <img src={pic.thumbnail} />}
                                <div>{pic.title}</div>
                            </PictureElement>
                        ))}
                </Left>
                <Center></Center>
                <Right></Right>
            </Body>
        </Container>
    )
}

export default Home

const Header = styled.div``

const PictureElement = styled.div`
    margin-bottom: 2rem;
    display: flex;
    flex-direction: column;
`

const Container = styled.div`
    display: flex;
    flex-direction: column;
    max-width: 1200px;
`

const Left = styled.div`
    display: flex;
    flex-direction: column;
    max-width: 250px;
`

const Center = styled.div``
const Right = styled.div``

const Body = styled.div`
    display: flex;
`
